SELECT category.name as child_name,category.id as child_id,category.category_ebay_id as child_ebay_id, category.level as child_level, category.best_offer as child_offer,parents.parent_name,parent as parent_id, parents.parent_ebay_id, parents.parent_level, parents.parent_offer FROM category JOIN (SELECT category.name as parent_name, category.category_ebay_id as parent_ebay_id, category.level as parent_level, category.best_offer as parent_offer,category_relation.parent_id as parent, category_relation.child_id as child FROM category_relation LEFT JOIN category ON category.id = parent) as parents ON parents.child = category.id